public function validarVisita($id_paciente,$id_especialidad,$fecha_solicitud){
$sql ="select vi.id_visita from solicitar so,visita vi, agendar age where
so.id_visita=vi.id_visita and age.id_visita= vi.id_visita and
age.id_especialidad= $id_especialidad and so.fecha_solicitud= '$fecha_solicitud' and
so.id_p=$id_paciente;";
$consulta= mysqli_query(Conectar::con(),$sql)
 or die("ERROR en la consulta $sql".mysqli_error(Conectar::con()));
if (mysqli_num_rows($consulta)==0){
    return true;
}
return false;
}

