public function returnid($ema, $pass)
    {
        $sql = "SELECT id_m from medico where correo_m='$ema' and pass_m='$pass'";
        $result = mysqli_query(Conectar::con(), $sql)
            or die("ERROR en la consulta $sql" . mysqli_error(Conectar::con()));
        $extraido1 = mysqli_fetch_array($result);
        if ($extraido1 != null) {
            $id = $extraido1[0];
        } else {
            $id = null;
        }
        return $id;
    }
    public function getNombre($id)
    {
        $sql = "SELECT nombre_m from medico where id_m=$id";
        $result = mysqli_query(Conectar::con(), $sql)
            or die("ERROR en la consulta $sql" . mysqli_error(Conectar::con()));
        $extraido1 = mysqli_fetch_array($result);
        if ($extraido1 != null) {
            $nombre = $extraido1[0];
        } else {
            $nombre = null;
        }
        return $nombre;
    }

    public function AgendaM($idU)
    {
        $sql1 = "SELECT e.nombre_e,p.id_p,p.nombre_p,p.apellidos_p, 
        p.telefono_p,est.nombre_est,v.id_visita
        FROM especialidad e,medico m, agendar a, visita v,
        solicitar s, paciente p, estar es,estado est
        WHERE e.id_especialidad=m.id_especialidad and
        m.id_m=a.id_medico and a.id_visita=v.id_visita
        and v.id_visita=s.id_visita and s.id_p=p.id_p
        and v.id_visita=es.id_visita and es.id_estado=1
        and es.id_estado=est.id_estado and m.id_m=$idU";

        $res1 = mysqli_query(Conectar::con(), $sql1);
        while ($row1 = mysqli_fetch_assoc($res1)) {
            $this->agenda[] = $row1;
        }
        return $this->agenda;
    }

    public function AgendadoM($idA)
    {
        $sql1 = "SELECT p.id_p,p.nombre_p,p.apellidos_p, 
        p.telefono_p,est.nombre_est,v.id_visita,v.fecha_v,v.hora_v
        FROM especialidad e,medico m, agendar a, visita v,
        solicitar s, paciente p, estar es,estado est
        WHERE e.id_especialidad=m.id_especialidad and
        m.id_m=a.id_medico and a.id_visita=v.id_visita
        and v.id_visita=s.id_visita and s.id_p=p.id_p
        and v.id_visita=es.id_visita and es.id_estado=2
        and es.id_estado=est.id_estado and m.id_m=$idA";

        $res1 = mysqli_query(Conectar::con(), $sql1);
        while ($row1 = mysqli_fetch_assoc($res1)) {
            $this->agendado[] = $row1;
        }
        return $this->agendado;
    }

